import React, { useState } from 'react'
import { NavLink,useNavigate  } from 'react-router-dom';
import { LogOut, reset } from "../features/authSlice";
import { useDispatch, useSelector } from "react-redux";
import logo from '../images/logo-dash.svg';
import { HomeIcon,TickSquareIcon, CalendarIcon, DocumentIcon, UsersIcon,AddUserIcon, LocationIcon, ScanIcon, RaportIcon, ActivityIcon, LogoutIcon, LogoIcon, LogoText } from '../common/icons/icons';
const Sidebar = (props) => {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const { user } = useSelector((state) => state.auth);

    const logout = () => {
        dispatch(LogOut());
        dispatch(reset());
        navigate("/");
    };

    const sidebarStyles = {
        width: props.isSidebarOpen ? '250px' : '50px',
        transition: 'width 0.3s ease'
      };
      const sidebarClass = props.isSidebarOpen ? "" : "sidebar-small";
      
    


    
    return (
        
       

        <aside className={`aside ${sidebarClass}`} style={sidebarStyles}>
            <div className="nav-logo">
                <NavLink className="nav-logo-a" to="/dashboard"><LogoIcon className={"sidebar-logo-icon"} /><LogoText  /></NavLink>
            </div>
            <div className="nav-content">
            <ul className="nav-ul">
                <li className="sidebar-menu-li">
                    <NavLink className="nav-a"  to="/dashboard">
                        <HomeIcon  className="nav-icon"/>
                        <span className="nav-text" style={menuStylesReSize} > Strona główna</span>
                    </NavLink>
                </li>
                <li className="sidebar-menu-li">
                    <NavLink className="nav-a" to="">
                        <UsersIcon  className="nav-icon"/>
                    <span  className={"nav-text dropdown-btn " + (isActive ? "active" : "")}  onClick={toggleDropdown} >Pracownicy</span>
                    </NavLink>
                    <ul class="sidebar-submenu-lu dropdown-container" style={{ display: isDropdownOpen ? "block" : "none" }}>
                        <li className='sidebar-submenu-li'><NavLink className="nav-a-dropdown" to="">Pracownicy</NavLink></li>
                        <li className='sidebar-submenu-li'><NavLink className="nav-a-dropdown" to="">Usuń pracownika</NavLink></li>
                        <li className='sidebar-submenu-li'><NavLink className="nav-a-dropdown" to="">Dodaj użytkownika</NavLink></li>
                    </ul>
                </li>
            </ul>
            {/* {user && user.role === "admin" && (
                <Div jakis tam menu dostepne dla admina
            )} */}
                <button  onClick={logout}  className="nav-button"><LogoutIcon className="nav-icon"  /><span className="nav-text" >Wyloguj się</span></button>
            </div>
            <div className="nav-footer">

            </div>
        </aside>

      
    )
}

export default Sidebar
