import React from 'react'
import { NavLink,useNavigate  } from 'react-router-dom';
import { LogOut, reset } from "../features/authSlice";
import { useDispatch, useSelector } from "react-redux";
import logo from '../images/logo-dash.svg';
import { HomeIcon,TickSquareIcon, CalendarIcon, DocumentIcon, UsersIcon,AddUserIcon, LocationIcon, ScanIcon, RaportIcon, ActivityIcon, LogoutIcon } from '../common/icons/icons';
const Sidebar = (props) => {
    
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const { user } = useSelector((state) => state.auth);

    const logout = () => {
        dispatch(LogOut());
        dispatch(reset());
        navigate("/");
    };

    const sidebarStyles = {
        width: props.isSidebarOpen ? '250px' : '50px',
        transition: 'width 0.3s ease'
      };

