        setIsTextVisible(!setIsTextVisible);
      }
    return (
        
       

        <aside className='aside' style={sidebarStyles}>
            <div className="nav-logo">
                <NavLink className="nav-logo-a" to="/dashboard"><img className="logo" src={logo} alt="Logo CourierHR" /></NavLink>
            </div>
            <div className="nav-content">
            <ul className="nav-ul">
                <li><NavLink className="nav-a" to="/dashboard"><HomeIcon className="nav-icon"/> {isTextVisible && "Strona główna" }</NavLink></li>
                <li><NavLink className="nav-a" to="/users"><UsersIcon className="nav-icon"/> Pracownicy</NavLink></li>


            </ul>
            {/* {user && user.role === "admin" && (
                <Div jakis tam menu dostepne dla admina
            )} */}


                <button  onClick={logout}  className="nav-button"><LogoutIcon className="nav-icon" />Wyloguj się</button>
            </div>
            <div className="nav-footer">

            </div>

        </aside>

      
    )
}

export default Sidebar
