import React from 'react'
import { NavLink,useNavigate  } from 'react-router-dom';
import { LogOut, reset } from "../features/authSlice";
import { useDispatch, useSelector } from "react-redux";
import logo from '../images/logo-dash.svg';
import { HomeIcon,TickSquareIcon, CalendarIcon, DocumentIcon, UsersIcon,AddUserIcon, LocationIcon, ScanIcon, RaportIcon, ActivityIcon, LogoutIcon } from '../common/icons/icons';
const Navbar = () => {
    const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user } = useSelector((state) => state.auth);

    const logout = () => {
        dispatch(LogOut());
        dispatch(reset());
        navigate("/");
    };

    const [showMenu, setShowMenu] = useState(false);
    const [sidebarClosed, setSidebarClosed] = useState(false);
  
    function handleArrowClick() {
      setShowMenu(!showMenu);
    }
  
    function handleSidebarClick() {
      setSidebarClosed(!sidebarClosed);
    }
  

    return (
        <div>
        <div className={`container ${sidebarClosed ? 'close' : ''}`}>
      <nav className="navbar">
        <a href="#" className="logo">MyLogo</a>
        <div className="sidebar">
          <span className="bx bx-menu" onClick={handleSidebarClick}></span>
          <ul className="menu">
            <li className="menu-item">
              <a href="#">Home</a>
            </li>
            <li className="menu-item">
              <a href="#">About</a>
              <span className="arrow" onClick={handleArrowClick}></span>
              <ul className={`submenu ${showMenu ? 'showMenu' : ''}`}>
                <li className="submenu-item"><a href="#">Our Company</a></li>
                <li className="submenu-item"><a href="#">Our Team</a></li>
                <li className="submenu-item"><a href="#">Our Mission</a></li>
              </ul>
            </li>
            <li className="menu-item">
              <a href="#">Services</a>
            </li>
            <li className="menu-item">
              <a href="#">Contact Us</a>
            </li>
          </ul>
        </div>
      </nav>
    </div>
</div>

                
      
    )


}

export default Navbar
