import React from 'react'
import { NavLink,useNavigate  } from 'react-router-dom';
import { LogOut, reset } from "../features/authSlice";
import { useDispatch, useSelector } from "react-redux";
import logo from '../images/logo-dash.svg';
import { HomeIcon,TickSquareIcon, CalendarIcon, DocumentIcon, UsersIcon,AddUserIcon, LocationIcon, ScanIcon, RaportIcon, ActivityIcon, LogoutIcon, LogoIcon, LogoText } from '../common/icons/icons';
const Sidebar = (props) => {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const { user } = useSelector((state) => state.auth);

    const logout = () => {
        dispatch(LogOut());
        dispatch(reset());
        navigate("/");
    };

    const sidebarStyles = {
        width: props.isSidebarOpen ? '250px' : '50px',
        transition: 'width 0.3s ease'
      };
    const menuStylesReSize  = {
        display: props.isSidebarOpen ? 'block' : 'none',
        transition: 'width 0.3s ease'
    };
    const setStyleIcon = {
        width: props.isSidebarOpen ? '20px' : '24px',
        height: 'auto',
        margin: 'auto'
 
    }
    


    
    return (
        
       

        <aside className='aside' style={sidebarStyles}>
            <div className="nav-logo">
                <NavLink className="nav-logo-a" to="/dashboard"><LogoIcon /><LogoText style={menuStylesReSize} /></NavLink>
            </div>
            <div className="nav-content">
            <ul className="nav-ul">
                <li><NavLink className="nav-a"  to="/dashboard"><HomeIcon style={setStyleIcon} className="nav-icon"/><span className="nav-text" style={menuStylesReSize} > Strona główna</span></NavLink></li>
                <li>
                    <NavLink className="nav-a" to="/users"><UsersIcon style={setStyleIcon} className="nav-icon"/><span className="nav-text" style={menuStylesReSize} >Pracownicy</span></NavLink>
                    <ul class="sidebar-submenu">
                        <li className='sidebar-sumenu-li'><NavLink className="nav-a-dropdown" to="">Dodaj pracownika</NavLink></li>
                        <li className='sidebar-sumenu-li'><NavLink className="nav-a-dropdown" to="">Usuń pracownika</NavLink></li>
                        <li className='sidebar-sumenu-li'><NavLink className="nav-a-dropdown" to="">Dodaj użytkownika</NavLink></li>
                    </ul>
                </li>
            </ul>
            {/* {user && user.role === "admin" && (
                <Div jakis tam menu dostepne dla admina
            )} */}
                <button  onClick={logout}  className="nav-button"><LogoutIcon className="nav-icon" style={setStyleIcon} /><span className="nav-text" style={menuStylesReSize} >Wyloguj się</span></button>
            </div>
            <div className="nav-footer">

            </div>
        </aside>

      
    )
}

export default Sidebar
