import React, { useState, useEffect } from "react";
import axios from "axios";
import { AvatarWoman, AvatarMan, SettingTwoIcon, SettingThreeIcon, ArrowDown } from "../common/icons/icons.jsx";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";

const TableEmployee = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const deleteUser = async (userId) => {
    try {
      await axios.delete(`http://localhost:5000/users/${userId}`);
      getUsers();
    } catch (error) {
      console.error(error);
    }
  };

  const [selectedRow, setSelectedRow] = useState(null);
  const handleRowClick = (index) => {
    if (selectedRow === index) {
      setSelectedRow(null);
    } else {
      setSelectedRow(index);
    }
  };

  return (
    <div>
      <div className="div-table">
        <table className="employee-table">
          <thead>
            <tr className="employee-tr">
              <th>Imie Nazwisko</th>
              <th>Stanowisko</th>
              <th>Oddział</th>
              <th>Email</th>
              <th>Doświadczenie</th>
              <th className="employee-setting">Opcje</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <React.Fragment key={user.uuid}>
                <tr key={user.uuid} className="employee-tr" onClick={() => setSelectedRow(index)}>
                  <td className="employee-profile td-employee">
                    {user.sex === "Kobieta" ? (
                      <div className="circle-avatar-women">{user && `${user.name.charAt(0).toUpperCase()}${user.surname.charAt(0).toUpperCase()}`}</div>
                    ) : (
                      <div className="circle-avatar-man">{user && `${user.name.charAt(0).toUpperCase()}${user.surname.charAt(0).toUpperCase()}`}</div>
                    )}
                    {user && `${user.name} ${user.surname}`}
                  </td>
                  <td className="td-employee">{user.position} {user.role}</td>
                  <td className="td-employee">Mników</td>
                  <td className="td-employee">{user.email}</td>
                  <td className="td-employee">1rok</td>
                  <td className="td-employee employee-setting">
                    <button>{selectedRow === index ? "Hide" : "Show"}<ArrowDown className="employee-icon"/></button>
                  </td>
                </tr>
                {selectedRow === index && (
                  <tr className="employee-tr-info">
                    <td colSpan="6">
                      <div className="employee-info">
                        <div>Adres: {user.address}</div>
                        <div>Telefon: {user.phone}</div>
                        <div>Data zatrudnienia: {user.employmentDate}</div>
                      </div>
                      <div className="employee-options">
                        <Link to={`/users/edit/${user.uuid}`} className="button is-small is-info">
                          Edit
                        </Link>
                        <button onClick={() => deleteUser(user.uuid)} className="button is-small is-danger">Delete
                        </button>
                  </div>
                </td>
              </tr>
            )}
          </React.Fragment>
        ))}
      </tbody>
    </table>
  </div>
</div>
);
};

export default TableEmployee;
